/**
 * Exported @arcgis/core types for Stencil.
 * This is to avoid the __esri namespace types
 * and satisfy the Stencil typing requirements
 * for builds.
 */
import type Basemap from "@arcgis/core/Basemap";
import type Graphic from "@arcgis/core/Graphic";
import type FeatureLayer from "@arcgis/core/layers/FeatureLayer";
import type GraphicsLayer from "@arcgis/core/layers/GraphicsLayer";
import type Layer from "@arcgis/core/layers/Layer";
import type MapView from "@arcgis/core/views/MapView";
import type SceneView from "@arcgis/core/views/SceneView";
export type ArcGISBasemap = InstanceType<typeof Basemap>;
export type ArcGISGraphic = InstanceType<typeof Graphic>;
export type ArcGISMapView = InstanceType<typeof MapView>;
export type ArcGISSceneView = InstanceType<typeof SceneView>;
export type ArcGISPortalLayer = InstanceType<typeof Layer>;
export type ArcGISFeatureLayer = InstanceType<typeof FeatureLayer>;
export type ArcGISGraphicsLayer = InstanceType<typeof GraphicsLayer>;
